    public boolean isPasswordValid(String str){
        if( str.isEmpty() ){
            result = "Please enter your password";
            return false;
        }
        Pattern lengthPattern = Pattern.compile("^.{6,20}$");
        if( !lengthPattern.matcher(str).matches() ){
            result = "Password should contain 6~20 characters";
            return false;
        }
//        Pattern numPattern = Pattern.compile("^(?=.*[0-9]).{8,20}$");
//        if( !numPattern.matcher(str).matches() ){
//            result = "Password should contain one digit at least";
//            return false;
//        }
//        Pattern letterPattern = Pattern.compile("^(?=.*[a-z,A-Z]).{8,20}$");
//        if( !letterPattern.matcher(str).matches() ){
//            result = "Password should contain one letter at least";
//            return false;
//        }
        Pattern specialPattern = Pattern.compile("^(?=\\S+$).{6,20}$");
        if( !specialPattern.matcher(str).matches() ){
            result = "Password should not contain whitespace";
            return false;
        }
        return true;
    }

    public boolean isUsernameValid(String str){
        if( str.isEmpty() ){
            result = "Please enter your username";
            return false;
        }
        Pattern lengthPattern = Pattern.compile("^.{6,20}$");
        if( !lengthPattern.matcher(str).matches() ){
            result = "Username should contain 6~20 characters";
            return false;
        }
        Pattern specialPattern = Pattern.compile("^(?=.*[!@#$%^&+=\\[\\]]).{6,20}$");
        if( specialPattern.matcher(str).matches() ){
            result = "Username should not contain special characters";
            return false;
        }
        Pattern dotPattern = Pattern.compile("^(?=.*[.]).{6,20}$");
        if( dotPattern.matcher(str).matches() ){
            result = "Username should not contain '.'";
            return false;
        }
        return true;
    }

    public boolean isCountryValid(String str){
        if( str.isEmpty() ){
            result = "Please choose your country";
            return false;
        }
        return true;
    }


    public String getLastResult(){
        return result;
    }



    public static class InputException extends Exception {
        public InputException(String message) {
            super(message);
        }
    }




